from pathlib import Path
import shutil

# %% ../../nbs/CLI/utils_.ipynb 4
def mk_clean_dir(path):
    path = Path(path)
    if path.exists():
        shutil.rmtree(path)
    path.mkdir(parents=True,exist_ok=True)
